import React from 'react'
import PropTypes from 'prop-types'
import Dots from '../../Slideshow/Dots'

const MementoView = ({ owner, id, numPhotos, title, description }) => {
    return <div class="column is-one-quarter"

    >
        <figure className="image container" style={{ width: '100%' }}>
            <img style={{
                borderStyle: 'groove'
            }}
                src={`http://localhost:5000/photos/${owner}/${id}`} alt="" />
            <Dots orientation={{
                bottom: 0,
                position: 'absolute',
                right: '50%'
            }} numSlides={numPhotos}
            />
            <a style={{
                position: 'absolute',
                top: 0,
                left: 0,
                backgroundColor: 'hsl(0, 0%, 96%)'
            }} className="subtitle"
                href={`/citypage/${title.split(',')[0]}`}
            > {title}</a>
        </figure>
        <div class="control">
            <textarea class="textarea" readonly>This content is readonly</textarea>
        </div>
        <div class="control">
            <textarea class="textarea has-fixed-size" placeholder="Fixed size textarea"></textarea>
        </div>
    </div>
}

MementoView.propTypes = {
    isEmpty: PropTypes.bool.isRequired,
    owner: PropTypes.string.isRequired
}

export default MementoView
