import NavbarView from './Navbar-view'
import axios from 'axios'
import { Redirect } from 'react-router-dom'
import PropTypes from 'prop-types'
import React from 'react'
import { userHasLoggedOut } from '../../actions';

const Navbar = ({ active, authenticationError, toggleSignInButton, toggleSignUpButton, userHasLoggedOut }) => {
    return <NavbarView active={active}
        onProfileClick={() => {
            return active ? <Redirect to="/profile" /> :
                authenticationError()
        }} onSigninClick={() => {
            if (active) {
                // logout
                axios.get('http://localhost:3001/api/users/logout').catch(err => alert(err))
                userHasLoggedOut()
                return
            } else {
                toggleSignInButton()
            }
        }} onSignupClick={() => toggleSignUpButton()} />
}

Navbar.propTypes = {
    active: PropTypes.bool.isRequired,
    authenticationError: PropTypes.func.isRequired,
    toggleSignInButton: PropTypes.func.isRequired,
    toggleSignUpButton: PropTypes.func.isRequired,
    userHasLoggedOut: PropTypes.func.isRequired
}

export default Navbar
