import React from 'react'
import PropTypes from 'prop-types'
import Slide from './Slide/Slide'
import Dots from './Dots'
import Caption from './Caption'

const Slideshow = ({ slides, nextSlide, position, prevSlide, id, isBox }) => {
    function scroll(event) {
        if (!isBox) {
            event.preventDefault();
            if (event.deltaY < 0 && position > 0) {
                // up
                prevSlide(id)
            }
            else if (event.deltaY > 0 && position < slides.length - 1) {
                // down
                nextSlide(id)
            }
        }
    }
    window.onwheel = scroll;
    return (
        <div>
            <div onClick={() => {
                if (position < slides.length - 1) {
                    nextSlide(id)
                }
            }}>
                {slides.map((slide, index) => {
                    return <Slide key={index}
                        active={position === index} image={slide.image} isBox
                    />
                })}
                <div className="captions">
                    {slides.map((caption, index) => {
                        return <Caption key={index}
                            active={position === index} title={caption.title} subtitle={caption.subtitle}
                        />
                    })}
                </div>
            </div>
            <Dots orientation={{
                top: '40%',
                position: 'absolute',
                right: 0
            }} numSlides={slides.length}
                id={id} />
        </div>
    )
}

Slideshow.propTypes = {
    slides: PropTypes.arrayOf(
        PropTypes.shape({
            image: PropTypes.string.isRequired,
            title: PropTypes.string.isRequired,
            subtitle: PropTypes.string.isRequired
        }).isRequired
    ).isRequired,
    nextSlide: PropTypes.func.isRequired,
    prevSlide: PropTypes.func.isRequired,
    position: PropTypes.number.isRequired
}

export default Slideshow
