import React from 'react';
import { Formik, Form, Field } from 'formik'
import 'bulma/css/bulma.css'
import Debug from '../../utils/Debug'
import * as Yup from "yup"
import axios from "axios"
import Input from './Input'



const Signin = () => {
    return <div>
        <h1 className="title is-4">Signin</h1>
        <Formik
            initialValues={{
                email: 'jasonli@mail.com',
                password: 'Password1',
                success: ''
            }}
            onSubmit={(values, actions) => {
                axios.post('http://localhost:3001/api/users/login', values).then((res) => {
                    if (res.status === 200) {
                        actions.setFieldValue('success', 'Success!')
                    } else {
                        alert(res)
                    }
                }).catch((err) => {
                    if (err.response) {
                        actions.setErrors(err.response.data)
                    } else {
                        alert(err)
                    }
                })
                actions.setSubmitting(false)
            }
            }
            render={({ values, isSubmitting }) => (
                <Form>
                    <Field name="email" component={Input} type="email" placeholder="e.g. jasonli@frontend.io" icons={{ left: 'envelope', right: 'check' }} />
                    <Field name="password" component={Input} type="password" placeholder="hhhhhhhh" icons={{ left: 'key', right: 'check' }} />
                    <button className="button is-link" type="submit" disabled={isSubmitting}>Submit</button>
                    {values.success && <article className="message is-success is-small">
                        <div className="message-body">
                            {values.success}
                        </div>
                    </article>}
                    <Debug />
                </Form>
            )}
        />
    </div>
}

export default Signin
