// Render Prop
import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik'
import 'bulma/css/bulma.css'
import { FormikConsumer } from 'formik'
import Debug from '../../utils/Debug'
import * as Yup from "yup"
import axios from "axios"



const SignupSchema = Yup.object().shape({
    username: Yup.string()
        .min(6, 'username must be at least 6 characters')
        .max(24, 'username cannot be greater than 20 characters')
        .required('username is required'),
    email: Yup.string()
        .email('Invalid email')
        .required('Required'),
    password: Yup.string()
        .min(6, 'password must be at least 6 characters')
        .max(20, 'password cannot be greater than 20 characters')
        .matches(/^(?=.*[a-z])/g, 'The password must contain at least 1 lowercase alphabetical character')
        .matches(/^(?=.*[A-Z])/g, 'The password must contain at least 1 uppercase alphabetical character')
        .matches(/^(?=.*[0-9])/g, 'The password must contain at least 1 numeric character')
        .required('Required')
});



const CustomInputComponent = ({
    field, // { name, value, onChange, onBlur }
    form: { touched, errors }, // also values, setXXXX, handleXXXX, dirty, isValid, status, etc.
    ...props
}) => (
        <div>
            <input type="text" {...field} {...props} />
            {touched[field.name] &&
                errors[field.name] && <div className="error">{errors[field.name]}</div>}
        </div>
    );

const Signup = () => {
    return <div>
        <Formik
            initialValues={{
                username: 'Jasonli',
                email: 'jasonli@mail.com',
                password: 'Password1',
            }}
            validationSchema={SignupSchema}
            onSubmit={(values, actions) => {
                console.log(values)
                // console.log(actions)

                axios.post('http://localhost:3001/api/users', values).then((res) => {
                    if (res.status === 200) {

                    }
                }).catch((err) => {
                    console.log(err)
                })
                actions.setSubmitting(false)
            }
            }
            render={({ isSubmitting }) => (
                <Form>
                    <Field name="firstName" component={CustomInputComponent} placeholder="First Name" />
                    <Field className="input" name="username" placeholder="username" />
                    <ErrorMessage name="username" />
                    <Field className="input" type="email" name="email" placeholder="email" />
                    <ErrorMessage name="email" />
                    <Field className="input" type="password" name="password" placeholder="password" />
                    <ErrorMessage name="password" />
                    <button className="button is-link" type="submit" disabled={isSubmitting}>Submit</button>
                    <Debug />
                </Form>
            )}

        />
    </div>
}

export default Signup
