import { connect } from 'react-redux'
import Drawer from './Drawer-container'

const mapStateToProps = state => {
    const { username, profilePicture, _id } = state.perspective.owner
    return {
        perspective: (state.perspective.owner === state.perspective.viewer) &&
            (typeof (state.perspective.owner) === 'string') &&
            (typeof (state.perspective.owner) === typeof (state.perspective.viewer)),
        owner: { username, profilePicture, _id }
    }
}

export default connect(mapStateToProps)(Drawer)
