const perspective = (state = {
    owner: false,
    viewer: false
}, action) => {
    // assume the page is being viewed by an outsider
    switch (action.type) {
        case 'OWNER':
            return { ...state, owner: state.id }
        case 'VIEWER':
            return { ...state, viewer: state.id }
        default:
            return state
    }
}

export default perspective
