import React from 'react'
import axios from 'axios'
import NavbarOther from '../../components/Navbar/NavbarOther'
import { connect } from 'react-redux'
import { setSignedIn, setViewer, setOwner } from '../../actions'
import Footer from '../../components/Footer'
import Drawer from '../../components/Drawer'

const ProfileView = ({ setSignedIn, setOwner, setViewer, match }) => {

    axios.get('/users').then(res => {
        setSignedIn(res.data._id)
        setViewer(res.data._id)

    }).catch(err => alert(err))

    // match.params.id is the visitor
    // objective is to compare the visitor with the owner
    axios.get(`/users/${match.params.id}`).then(res => {
        setOwner(res.data)
    }).catch(err => alert(err))

    return (
        <div>
            <NavbarOther />
            <section class="hero is-fullheight-with-navbar">
                <div id="hero-body" class="hero-body">
                    <div className='columns'>
                        <Drawer location='/profile' />
                    </div>
                </div>
            </section>
            <Footer />
        </div>
    )
}


const mapDispatchToProps = dispatch => {
    return {
        setSignedIn: id => dispatch(setSignedIn(id)),
        setOwner: id => dispatch(setOwner(id)),
        setViewer: id => dispatch(setViewer(id))
    }
}

export default connect(null, mapDispatchToProps)(ProfileView)
