import React from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import Landing from './routes/Landing'
import Homeglobe from './routes/Homeglobe'
import { library } from '@fortawesome/fontawesome-svg-core'
import { faUser, faEnvelope, faKey, faCheck } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'
import { connect } from 'react-redux'
import { signedIn } from './actions'


library.add(faUser, faEnvelope, faKey, faCheck)

const App = ({ signedIn }) => {
    axios.get('http://localhost:3001/api/users').then(res => {
        if (res.status === 200) {
            signedIn(res.data)
            return (
                <Router>
                    <Route exact path="/" component={Landing} />
                    <Route path="/homeglobe" component={Homeglobe} />
                </Router>
            )
        } else {
            alert(res)
        }
    }).catch(err => alert(err))

}

const mapDispatchToProps = dispatch => {
    return {
        signedIn: id => dispatch(signedIn(id))
    }
}

export default connect(null, mapDispatchToProps)(App)
