function toggles(state = {
    signin: true,
    signup: true,
    navbar: false,
    profile: true
}, action) {
    switch (action.type) {
        case 'TOGGLE_SIGNIN_BUTTON':
            return { ...state, signin: !state.signin }
        case 'TOGGLE_SIGNUP_BUTTON':
            return { ...state, signup: !state.signup }
        case 'SIGNED_IN':
            return {
                signin: false,
                signup: false,
                navbar: true,
                profile: true
            }
        case 'NOT_SIGNED_IN':
            return {
                signin: true,
                signup: true,
                navbar: false,
                profile: false
            }
        case 'USER_HAS_LOGGED_OUT': //logout button on navbar was clicked
            return {
                signin: true,
                signup: true,
                navbar: false,
                profile: false
            }
        default:
            return state
    }
}

export default toggles
