import React from 'react'
import PropTypes from 'prop-types'
import Dots from '../../Slideshow/Dots'

const MementoView = ({ owner, id, numPhotos, title, description, active, onClick }) => {

    return <div class="column is-one-quarter"
        style={{
            borderStyle: 'groove'
        }}
        onClick={onClick}
    >
        <figure id="info" className="image container" style={{ width: '100%' }}>
            <img
                src={`http://localhost:5000/photos/${owner}/${id}`} alt="" />
            <Dots orientation={{
                bottom: 0,
                position: 'absolute',
                right: '50%'
            }} numSlides={numPhotos}
            />
            <a style={{
                position: 'absolute',
                top: 0,
                left: 0,
                backgroundColor: 'hsl(0, 0%, 96%)'
            }} className="subtitle"
                href={`/citypage/${title.split(',')[0]}`}
            > {title}</a>
        </figure>
        <div className="content is-small" style={{
            backgroundColor: 'hsl(0, 0%, 96%)'
        }}>
            {description.length > 150 ? description.slice(150) + '...' : description}
        </div>
        <div className={active ? "modal is-active" : "modal"}>
            <div class="modal-background"></div>
            <div class="modal-content">
                WHAT UP
                </div>
            <button class="modal-close is-large" aria-label="close"></button>
        </div>

    </div>
}

MementoView.propTypes = {
    isEmpty: PropTypes.bool.isRequired,
    owner: PropTypes.string.isRequired
}

export default MementoView
