import React from 'react';
import { Formik, Form, Field } from 'formik'
import * as Yup from "yup"
import axios from "axios"
import Input from '../Forms/Input'

const ChangeUsernameSchema = Yup.object().shape({
    username: Yup.string()
        .min(6, 'username must be at least 6 characters')
        .max(24, 'username cannot be greater than 20 characters')
        .required('username is required')
})


const ChangeSettingsView = ({ email, username }) => {
    return <div class="column">
        <h2 className="subtitle"> Email: </h2>
        <b> {email} </b>
        <div class="columns is-mobile">
            <div class="column">
                <Formik
                    initialValues={{
                        username: '',
                        success: ''
                    }}
                    validationSchema={ChangeUsernameSchema}
                    onSubmit={(values, actions) => {
                        // axios.post
                    }}
                    render={({ }) => {
                        <Form>
                            <Field name="username" component={Input} type="text" placeholder={username} icons={{ left: 'user', right: 'check' }} />
                            <button className="button is-link" type="submit" disabled={isSubmitting}>Change Username</button>
                            {values.success && <article className="message is-success is-small">
                                <div className="message-body">
                                    {values.success}
                                </div>
                            </article>}
                        </Form>
                    }}
                />
                <p class="bd-notification is-info">First nested column</p>
            </div>
            <div class="column">
                <p class="bd-notification is-info">Second nested column</p>
            </div>
        </div>
    </div>
}

export default ChangeSettingsView
