// /src/actions/index
// POSITION
export const nextSlide = (index) => {
    return {
        type: 'NEXT_SLIDE',
        index
    }
}

export const prevSlide = (index) => {
    return {
        type: 'PREV_SLIDE',
        index
    }
}

export const skipTo = (index) => {
    return {
        type: 'SKIP_TO',
        index
    }
}

// ERRORS
export const authenticationError = () => {
    return {
        type: 'ERR_SIGNED_IN'
    }
}

export const errorCleared = () => {
    return {
        type: 'ERR_SIGNED_IN_CLEARED',
    }
}

export const toggleSignInButton = () => {
    return {
        type: 'TOGGLE_SIGNIN_BUTTON'
    }
}

export const toggleSignUpButton = () => {
    return {
        type: 'TOGGLE_SIGNUP_BUTTON'
    }
}

export const signedIn = (id) => {
    if (id) {
        return {
            type: 'SIGNED_IN',
            id
        }
    }
    return {
        type: 'NOT_SIGNED_IN',
        id
    }
}

export const loadData = (data, label) => {
    return {
        type: label,
        data
    }
}

// PERSPECTIVE 
export const determinePerspective = (owner = false, viewer = false) => {
    if ((typeof (owner) === typeof (viewer)) &&
        (typeof (viewer) === 'string') && (viewer === owner)) {
        return {
            type: 'IN'
        }
    } else {
        return {
            type: 'OUT'
        }
    }
}
