const err = (state = {
    signin: false,
    repeat: { profile: false, edit: false }
}, action) => {
    switch (action.type) {
        case 'ERR_SIGNED_IN':
            // console.log({ ...state, signin: true })
            return { ...state, signin: true } // set display to on, message, touched
        case 'ERR_SIGNED_IN_CLEARED':
            console.log({ ...state, signin: false })
            return { ...state, signin: false }
        case 'ERR_REPEAT':
            return { ...state, repeat: true }
        default:
            return state
    }
}
export default err
