import React from 'react'
import ManageMementosView from './ManageMementos-view'
import axios from 'axios'

const ManageMementos = ({ owner, selectedPhotos, cities }) => {
    return <ManageMementosView owner={owner}
        createMemento={() => {
            const city = cities[document.querySelector('select').selectedIndex]
            const description = document.querySelector('textarea').value
            const photos = selectedPhotos
            axios.post(`/mementos/${owner}`, { description, photos, city })
                .catch(err => alert(err))
        }}
    />
}


export default ManageMementos
