import React, { Component } from 'react';
import * as am4core from "@amcharts/amcharts4/core";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";
import * as am4maps from "@amcharts/amcharts4/maps";
import worldHigh from '@amcharts/amcharts4-geodata/worldHigh'

am4core.useTheme(am4themes_animated);

class Globe extends Component {
    componentDidMount() {
        am4core.useTheme(am4themes_animated);
        // create map instance
        const chart = am4core.create("chartdiv", am4maps.MapChart);
        // Set map definition
        chart.geodata = worldHigh
        // setting projection
        chart.projection = new am4maps.projections.Orthographic();
        // Create map polygon series
        // represents map areas
        var polygonSeries = chart.series.push(new am4maps.MapPolygonSeries());
        polygonSeries.useGeodata = true;
        // configure appearance and behavior of its items by accessing templates
        var polygonTemplate = polygonSeries.mapPolygons.template;
        polygonTemplate.tooltipText = "{name}";
        polygonTemplate.fill = chart.colors.getIndex(0).lighten(0.5);
        var hs = polygonTemplate.states.create("hover");
        hs.properties.fill = chart.colors.getIndex(0);
        // var sample = polygonSeries.mapPolygons.template;
        // sample.tooltipText = "{name}";
        // sample.fill = am4core.color("#363636");
        // sample.propertyFields.fill = "color";
        polygonTemplate.fill = am4core.color("#363636");
        polygonTemplate.propertyFields.fill = 'color'
        // this.chart = chart;
    }

    componentWillUnmount() {
        if (this.chart) {
            this.chart.dispose();
        }
    }

    render() {
        return (
            <div id="chartdiv" style={{ width: "100%", height: "500px" }}></div>
        );
    }
}

export default Globe;
