import axios from 'axios'

var initialValue = () => axios.get('http://localhost:3001/api/users').then(res => {
    if (res.status === 200) {
        return res.data ? {
            signin: false,
            signup: false,
            navbar: true
        } : {
                signin: true,
                signup: true,
                navbar: false
            }
    }
}).catch(err => alert(err))




async function toggles(state = {
    signin: true,
    signup: true,
    navbar: false
}, action) {
    switch (action.type) {
        case 'TOGGLE_SIGNIN_BUTTON':
            return { ...state, signin: !state.signin }
        case 'SIGNED_IN':
            // console.log('i should be here')
            // console.log({ signin: false, signup: state.signup })
            // console.log({ ...state })
            // console.log({ ...state, signin: false }) stuff then overwrite
            return { ...state, signin: false, navbar: true }
        case 'USER_HAS_LOGGED_OUT':
            return {
                signin: true,
                signup: true,
                navbar: false
            }
        default:
            return state
    }
}

export default toggles
