const position = (state = 0, action) => {
    switch (action.type) {
        case 'NEXT_SLIDE':
            if (action.index + 1 <= 0) {
                return 0
            } else {
                return action.index + 1
            }
        case 'SKIP_TO':
            return action.index
        default:
            return state
    }
}

export default position
