import React from 'react'
import PropTypes from 'prop-types'
import Dots from '../../Slideshow/Dots'
import Slideshow from '../../Slideshow'

const MementoView = ({ owner, photos, title, description, active, onClick, id }) => {
    return <div class="column is-one-quarter"
        style={{
            borderStyle: 'groove'
        }}
    >
        <figure onClick={onClick} id="info" className="image container" style={{ width: '100%' }}>
            <Slideshow slides={photos.map(p => {
                return {
                    title: '',
                    subtitle: '',
                    image: `http://localhost:5000/photos/${owner}/${p}`
                }
            })} id={id} isBox={true} />
            <a style={{
                position: 'absolute',
                top: 0,
                left: 0,
                backgroundColor: 'hsl(0, 0%, 96%)'
            }} className="subtitle"
                href={`/citypage/${title.split(',')[0]}`}
            > {title}</a>
        </figure>
        <div className="content is-small" style={{
            backgroundColor: 'hsl(0, 0%, 96%)',
            margin: 0
        }}>
            {description.length > 150 ? description.slice(0, 150) + '...' : '...'}
        </div>
        <div className={active ? "modal is-active" : "modal"}>
            <div onClick={onClick} class="modal-background"></div>
            <div class="modal-content">
                <header class="modal-card-head">
                    <p class="modal-card-title">Modal title</p>
                </header>
                <Slideshow slides={photos.map(p => {
                    return {
                        title: '',
                        subtitle: '',
                        image: `http://localhost:5000/photos/${owner}/${p}`
                    }
                })} id={id} isModal={true} />
                <footer class="modal-card-foot">{description}</footer>

            </div>
            <button onClick={onClick} class="modal-close is-large" aria-label="close"></button>
        </div>

    </div>
}

MementoView.propTypes = {
    isEmpty: PropTypes.bool.isRequired,
    owner: PropTypes.string.isRequired
}

export default MementoView
